"use strict";let control=document.querySelectorAll(".widget_control"),controlPrev=document.querySelector(".widget_control.prev"),controlNext=document.querySelector(".widget_control.next"),container=document.querySelector(".widget_slide"),position=container.clientWidth+container.scrollLeft;function toggleControls(){position=container.offsetWidth+container.scrollLeft,container.scrollLeft<=10?controlPrev.classList.add("inactive"):controlPrev.classList.remove("inactive"),position>=container.scrollWidth-10?controlNext.classList.add("inactive"):controlNext.classList.remove("inactive")}control.forEach(t=>{t.addEventListener("click",()=>{t.classList.contains("prev")&&(container.scrollLeft-=container.clientWidth),t.classList.contains("next")&&(container.scrollLeft+=container.clientWidth)})}),toggleControls();let lastCall=!1;container.addEventListener("scroll",()=>{lastCall&&clearTimeout(lastCall),lastCall=setTimeout(()=>{toggleControls()},100)});
